mapper.select:
SELECT a.engagement_info_id pkInfoId, a.engagement_title title, a.start_time pktime, CONCAT(a.city_name,'-',a.district_name) region, a.address, a.cost_plan costContent, CASE WHEN NOW()> a.sign_up_end_time AND a.status = 0 THEN 2 ELSE a.status END pkStatus, CASE WHEN NOW()> a.sign_up_end_time AND a.status = 0 THEN '已过期' WHEN a.status = 0 THEN '待约战' ELSE '约战成功' END pkStatusStr, (SELECT title FROM class WHERE id = a.engagement_type) pkMarchType, a.team_id teamId, b.team_name teamName, b.team_logo logpPath, a.create_time teamTime FROM engagement_info a, team_info b WHERE a.team_id = b.team_info_id
AND a.city_id = #{paramEntity.cityId}
AND a.district_id = #{paramEntity.districtId}
AND a.engagement_type = #{paramEntity.pkMarchType}
AND NOW()> a.sign_up_end_time AND a.status = 0
AND a.engagement_type = #{paramEntity.pkStatus}
AND (a.engagement_title LIKE CONCAT('%',#{paramEntity.searchKey},'%') OR b.team_name LIKE CONCAT('%',#{paramEntity.searchKey},'%'))
AND a.create_time >= #{paramEntity.startDate}
AND a.create_time <= #{paramEntity.endDate}

mapper.update:
UPDATE 
	engagement_info a,engagement_team_apply b
SET 
	b.audit_status = 0
WHERE
	a.engagement_info_id = b.engagement_info_id
AND a.engagement_info_id = #{pkInfoId}
AND b.team_id = #{applyTeamId}
AND a.user_id = #{userId}
